<?php class hesapmakinesi{function t($s1,$s2){echo $s1+$s2;}function c($s1,$s2){echo $s1-$s2;}function cc($s1,$s2){echo $s1*$s2;}function b($s1,$s2){echo $s1/$s2;}function m($s1,$s2){echo $s1%$s2;}function ust($x,$y){$z=1;$l=$x;while($z<$y){$z=$z+1;$x=$x*$l;}echo $x;}}function yaz($de){echo $yaz;}function yaz_r($x){print_r($x);}function s_baslik($y){echo '<title>'.$y.'</title>';}class dosyalar{function dosya_cagir($k){include($k);}function dosya_cagir2($k){include_once($k);}function dosya_cagir3($k){require($k);}function dosya_cagir4($k){require_once($k);}function jsdosya($x){echo '<script type="text/javascript" src="'.$x.'" language="javascript"></script>';}function sdosya($t,$x,$t2){echo '<script type="'.$t.'" src="'.$x.'" language="'.$t2.'"></script>';}}function afmha(){phpinfo();}function engelle($z){if(!defined('CONFIG_CLASS_INCLUDED')){die($z);}}function sunucu(){print_r($_SERVER);}function rastgele($sayimiz1,$sayimiz2){$rastgelesayi=rand($sayimiz1,$sayimiz2);echo $rastgelesayi;}function tarih(){echo date('d.m.Y H:i:s');}class yazi_islemler{function e_slash($d){$e_slash=addslashes($d);global $e_slash;}function k_slash($d){$k_slash=stripslashes($d);global $k_slash;}function tumubuyuk($tb){$tumb=strtoupper($tb);global $tumb;}function tumukucuk($tk){$tumk=strtolower($tk);global $tumk;}function basbuyuk($bb){$basbuyuk=strtoupper($bb);global $basbuyuk;}function tumubasbuyuk($tbb){$tumbb=strtolower($tbb);global $tumubasbuyuk;}}class cookie{function olustur($a,$b,$c=""){if($c==""){setcookie($a,$b,time()+60*60*24);}else{setcookie($a,$b,$c);}global $cookie_afmha_cikti;if(isset($_COOKIE[$a])){$cookie_afmha_cikti="İşlem tamam.";}else {$cookie_afmha_cikti="İşlem sırasında bir hata meydana geldi.";}}function sil($a,$b,$c=" "){if($c==" "){setcookie($a,$b,'time() - 3600');}else{setcookie($a,$b,$c);}global $cookie_afmha_cikti;if(isset($_COOKIE[$a])){$cookie_afmha_cikti="İşlem sırasında bir hata meydana geldi..";}else {$cookie_afmha_cikti="İşlem tamam.";}}function kullan($a){global $cookie_afmha_cikti;if(isset($_COOKIE[$a])){global $cookie_afmha;$cookie_afmha=$_COOKIE[$a];}else {$cookie_afmha_cikti="Cookie yok.";}}}$co=new cookie();$hm=new hesapmakinesi();$d=new dosyalar();$yi=new yazi_islemler(); ?>
